for i in a:
    i = i.replace('https:', 'https..').replace('_(', '___')
    b = i.split(':')
    t = {
            "name": "",
            "categorie": "",
            "sensor": "",
            "url": "",
            "image": "",
            "url_code": "",
            "url_code_example": "",
            "liaison": "and",
            "info": []
        }
    for ii in range(len(b)):
        if ii == 0:
            t['categorie'] = b[ii][:-1]
        elif ii == 1:
            if '(' in b[ii]:
                aq = b[ii].split('(')
                t['name'] = aq[0][1:-1]
                t['sensor'] = aq[1][:-2]
            else:
                t['name'] = b[ii][1:-1]
        elif ii == 2:
            aq = b[ii].split('(')
            info = aq[0][:-1]
            infoI = info.split(',')
            for iii in infoI:
                ttt = {
                    "typePort": "",
                    "in_out": "",
                    "number": ""
                }
                infoII = iii.split(' ')
                infoII.pop(0)
                ttt['typePort'] = infoII[0].replace('digital', 'Digital').replace('analog', 'Analog')
                ttt['in_out'] = infoII[1]
                if len(infoII) == 3:
                    ttt['number'] = str(infoII[2][1:])
                else:
                    ttt['number'] = str(1)

                t['info'].append(ttt)

            if len(aq) == 3:
                t['url'] = aq[1][:-2].replace('https..', 'https:').replace('___', '_(')
                t['url_code_example'] = aq[2][:-1].replace('https..', 'https:').replace('___', '_(')
            else:
                t['url'] = aq[1][:-1].replace('https..', 'https:').replace('___', '_(')
    print(t,',')

